import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [credentials, setCredentials] = useState({
        username: '',
        password: ''
    });
    
    const handleChange = (e) => {
        const { name, value } = e.target;
        setCredentials((prevCredentials) => ({
            ...prevCredentials,
            [name]: value
        }));
    };

    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const responce = await axios.post('/login', credentials); 
            const email = responce.data.responce;
            if (email === "Invalid") {
                console.log("error");
            } else {
              navigate(`/profile?email=${email}`);
            }
        } catch(error) {
            console.error("Login Error:", error);
        }  
    };

  return (
    <div>
        <div className="left">
            <form className="intro-container" onSubmit={handleLogin}>
                <h1>Welcome</h1>
                    <p>Please enter your credentials</p>
                <div> 
                    <input 
                        type="text" 
                        id="username" 
                        name="username"
                        value={credentials.username}
                        onChange={handleChange}
                        placeholder="Username" 
                    />
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={credentials.password}
                        onChange={handleChange}
                        placeholder="Password" 
                    />
                    <div className="button-wrapper">
                        <button type="submit" className="universal-button">Login</button>
                    </div>
               
                </div>
            </form>
        </div>

        <div className="right">
            <div className="puzzle-img"></div>
        </div>
    </div>
  )
}

export default Login;
